package VarsAndOutput;

public class LevelTwoProblemFour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Level Two Problem Four
		int dividend = 10;//Declaring and Initializing the integer
		int divisor = 7; //Declaring and Initializing the integer
		int quotient = (dividend/divisor);//Storing the answer into a variable
		int remainder = (dividend % divisor);// Storing the remainder into a variable
		System.out.print("The quotient is: " + quotient + " and the remainder is: " + remainder);//Outputting the quotient and the remainder
	}

}
